import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom"; // Заменяем useHistory на useNavigate
import { addProduct } from "../redux/productsSlice";

const CreateProductPage: React.FC = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState("");
  const [price, setPrice] = useState("");
  const [status, setStatus] = useState<"active" | "archived">("active");

  const dispatch = useDispatch();
  const navigate = useNavigate(); // Используем useNavigate

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (title && description && image && price) {
      dispatch(
        addProduct({
          id: Date.now(),
          title,
          description,
          image,
          price: parseFloat(price),
          status,
        })
      );
      navigate("/products"); // Используем navigate вместо history.push
    } else {
      alert("All fields are required!");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Title"
        className="input input-bordered input-primary w-full max-w-xs block"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        required
      />
      <textarea
        placeholder="Описание"
        className="textarea textarea-primary w-full max-w-xs block"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        required
      />
      <input
        type="text"
        placeholder="Image URL"
        className="input input-bordered input-primary w-full max-w-xs block"
        value={image}
        onChange={(e) => setImage(e.target.value)}
        required
      />
      <input
        type="number"
        placeholder="Цена"
        className="input input-bordered input-primary w-full max-w-xs block"
        value={price}
        onChange={(e) => setPrice(e.target.value)}
        required
      />
      <select
        value={status}
        className="select select-primary w-full max-w-xs"
        onChange={(e) => setStatus(e.target.value as "active" | "archived")}
      >
        <option value="active">Active</option>
        <option value="archived">Archived</option>
      </select>
      <button type="submit" className="btn btn-primary">
        Сохранить
      </button>
      <button type="button" onClick={() => navigate("/products")}>
        Отм
      </button>{" "}
      {/* Используем navigate для отмены */}
    </form>
  );
};

export default CreateProductPage;
